mapper.select:
SELECT prod_nm , GROUP_CONCAT(concat_txt ORDER BY unit_ord, sell_quan) delivery_detail FROM ( SELECT d.prod_nm , b.cust_nm , c.sell_quan , e.unit_nm , d.prod_typ , f.ord , e.ord AS unit_ord , CONCAT(b.cust_al, c.sell_quan, e.unit_al) AS concat_txt FROM delivery_info a JOIN customer b ON a.truck_no = b.truck_no JOIN sell c ON b.cust_no = c.cust_no JOIN product d ON c.prod_no = d.prod_no JOIN unit e ON c.unit = e.unit LEFT JOIN ord f ON d.prod_no = f.prod_no WHERE c.sell_dt = DATE_FORMAT(#{sell_dt},'%Y%m%d') /*미입(N)제외 교환(E)은 무조건 작업표에만 포함시킴*/ AND IFNULL(c.sell_type, '') != 'N'
AND a.truck_no = #{truck_no}
) a GROUP BY prod_nm ORDER BY prod_typ, ord
