mapper.update:

	UPDATE product_price
	   SET end_dt = DATE_FORMAT(DATE(#{A}-1),'%Y%m%d')
	 WHERE cust_no = #{cust_no}
	   AND prod_no = #{prod_no}
	   AND prod_seq = #{prod_seq}

mapper.insert:
INSERT INTO product_price (start_dt
							, cust_no
							, prod_no
							, prod_seq
							, prod_price
							, end_dt
							) 
		VALUES
				(#{A}
				, (SELECT cust_no FROM customer WHERE cust_nm = #{B})
				, (SELECT prod_no FROM product WHERE prod_nm = #{C})
				, #{next_prod_seq}
				, #{D}
				, '99991231'
				)

mapper.select:
SELECT a.cust_no , b.cust_nm , a.prod_no , c.prod_nm , a.prod_seq , a.start_dt , a.end_dt , a.prod_price FROM product_price a JOIN customer b ON a.cust_no = b.cust_no JOIN product c ON a.prod_no = c.prod_no WHERE 1=1
AND DATE_FORMAT(DATE(#{srch_dt}),'%Y%m%d') BETWEEN a.start_dt AND a.end_dt
AND a.cust_no = #{cust_no}
AND a.prod_no = #{prod_no}

mapper.delete:
DELETE FROM product_price 
 WHERE cust_no = #{cust_no}
   AND prod_no = #{prod_no}
   AND prod_seq = #{prod_seq}

mapper.update:

	UPDATE product_price
	   SET end_dt = '99991231'
	 WHERE cust_no = #{cust_no}
	   AND prod_no = #{prod_no}
	   AND prod_seq = ( SELECT MAX(prod_seq)
					      FROM product_price
						 WHERE cust_no = #{cust_no}
	   				       AND prod_no = #{prod_no}
					  )


	UPDATE product_price
	   SET start_dt = DATE_FORMAT(DATE(#{start_dt}),'%Y%m%d')
	   	  , end_dt = DATE_FORMAT(DATE(#{end_dt}),'%Y%m%d')
	   	  , prod_price = #{prod_price}
	   	  , unit = #{unit}
	 WHERE cust_no = #{cust_no}
	   AND prod_no = #{prod_no}
	   AND prod_seq = #{prod_seq}
