mapper.update:
UPDATE sell 
   SET prod_no = #{prod_no}
	   , sell_quan = #{sell_quan}
	   , unit = #{unit}	
	   , truck_no = #{truck_no}			
 WHERE sell_dt = DATE_FORMAT(#{sell_dt},'%Y%m%d')
   AND cust_no = #{cust_no}
   AND sell_seq = #{sell_seq}

mapper.select:
SELECT DATE_FORMAT(a.sell_dt, '%Y-%m-%d') sell_dt , a.cust_no , CASE WHEN c.branch_nm IS NULL THEN c.cust_nm ELSE CONCAT(c.cust_nm, '-', IFNULL(c.branch_nm,'')) END cust_nm , a.sell_seq , d.prod_nm , a.sell_quan /*판매수량*/ , f.return_quan /*반품수량*/ , a.unit , e.unit_nm , a.unit_price , d.tax_yn , CASE WHEN d.tax_yn = 'N' THEN TRUNCATE(CASE WHEN a.unit_price > 0 THEN (a.sell_quan * a.unit_price) ELSE (a.sell_quan * a.prod_price) END , 2) ELSE 0 END tax_free /*면세*/ , CASE WHEN d.tax_yn = 'Y' THEN TRUNCATE(CASE WHEN a.unit_price > 0 THEN (a.sell_quan * a.unit_price) / 1.1 ELSE (a.sell_quan * a.prod_price) / 1.1 END , 2) ELSE 0 END supply /*공급가액*/ , CASE WHEN d.tax_yn = 'Y' THEN TRUNCATE((CASE WHEN a.unit_price > 0 THEN (a.sell_quan * a.unit_price) / 1.1 ELSE (a.sell_quan * a.prod_price) / 1.1 END ) /10 , 2) ELSE 0 END tax /*부가세*/ , CASE WHEN a.unit_price > 0 THEN a.sell_quan * a.unit_price ELSE a.sell_quan * a.prod_price END total_price /*계*/ FROM sell a JOIN customer c ON a.cust_no = c.cust_no LEFT JOIN product d ON a.prod_no = d.prod_no LEFT JOIN unit e ON a.unit = e.unit LEFT JOIN sell_return f ON a.sell_dt = f.sell_dt AND a.cust_no = f.cust_no AND a.sell_seq = f.sell_seq WHERE 1=1
AND a.sell_dt = DATE_FORMAT(#{sell_dt},'%Y%m%d')
AND a.cust_no = #{cust_no}
AND a.prod_no = #{prod_no}
ORDER BY a.sell_dt, c.cust_nm, a.sell_seq

SELECT COUNT(*) cnt
  FROM product
 WHERE prod_nm = #{prod_nm}

