mapper.update:

	UPDATE product_price
	   SET end_dt = DATE_FORMAT(DATE_ADD(NOW(), INTERVAL -1 DAY),'%Y%m%d')
	 WHERE cust_no = #{cust_no}
	   AND prod_no = #{prod_no}
